import React from 'react'
import './navigationBar.css';
import logoDark from '../../assets/logo-dark.png'
import logoLight from '../../assets/logo-white.png'
import sun from '../../assets/sun.png'
import moon from '../../assets/moon.png'

import { Link } from 'react-scroll'

function NavigationBar  ({ isDarkTheme, toggleTheme,projectsRef })  {
  // Scroll to the projects section when the link is clicked
  const scrollToProjects = () => {
    if (projectsRef.current) {
      projectsRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };
  return (
    <nav className="navigationBar">
      <img src={isDarkTheme? logoDark:logoLight} alt='Logo' className='Logo' />
      <div className='desktopMenu'>
        <Link className="desktopMenuListItem">HOME</Link>
        <Link className="desktopMenuListItem" onClick={ scrollToProjects}>MY PROJECTS</Link>
        <Link className="desktopMenuListItem">MY ART PORTFOLIO</Link>
      </div>
      <button onClick={toggleTheme} className="theme-toggle-btn">
        {/* Switch between sun and moon icons based on the current theme */}
        <img src={isDarkTheme ? moon : sun} className="theme-icon" alt="Toggle Theme" />
      </button>
    </nav >
  )
}

export default NavigationBar;
