import { useState, useEffect } from 'react';  // Import useState and useEffect hooks
import React from 'react'
import './navigationBar.css';
import logo from '../assets/logo-dark.png'
import sun from '../assets/sun.png'
import moon from '../assets/moon.png'

import { Link } from 'react-scroll'

const NavigationBar = () => {
  // Create a state to track the current theme
  const [isDarkTheme, setIsDarkTheme] = useState(false);

  // Function to toggle theme
  const toggleTheme = () => {
    console.log("Toggling theme..."); // Debugging log
    setIsDarkTheme((prevTheme) => !prevTheme); // Toggle state

    // Toggle 'dark-theme' class on the body
    document.body.classList.toggle('dark-theme');
  };
  return (
    <nav className="navigationBar">
      <img src={logo} alt='Logo' className='Logo' />
      <div className='desktopMenu'>
        <Link className="desktopMenuListItem">HOME</Link>
        <Link className="desktopMenuListItem">MY PROJECTS</Link>
        <Link className="desktopMenuListItem">MY ART PORTFOLIO</Link>
      </div>
      <button onClick={toggleTheme} className="theme-toggle-btn">
        {/* Switch between sun and moon icons based on the current theme */}
        <img src={isDarkTheme ? moon : sun} className="theme-icon" alt="Toggle Theme" />
      </button>
    </nav >
  )
}

export default NavigationBar
